<template>
  <div class="certinstall">
    <div class="cow-icon">
      <img v-if="install_step === 1 || install_step === 2" src="data:image/png;base64,">
      <img v-if="install_step === 3" src="data:image/png;base64,">
    </div>
    <div class="installing" v-if="install_step === 1">
      <span>正在安装数字证书...</span>
    </div>
    <div class="success-install" v-if="install_step === 2">
      <span class="cert-install-status">安装成功！</span>
      <v-nextstep :next="nextstep.next" :text="nextstep.text[0]" @goNext="allowNext"></v-nextstep>
    </div>
    <div class="fail-install" v-if="install_step === 3">
      <span class="cert-install-status">安装失败！</span>
      <v-nextstep :text="nextstep.text[1]" @goNext="reinstall" class="cert-reinstall"></v-nextstep>
      <div class="tel-box" @click = "call">
        <v-nextstep :text="nextstep.text[2]" class="contact-service"></v-nextstep>
        <a @click="doNothing" :href="tel_href" v-if="is_can_call"></a>
      </div>

    </div>
    <div class="middle-center">
      <v-alertbox :content="alertbox.content" v-if="alertbox.show" @closeAlertBox="closeAlertBox"></v-alertbox>
    </div>
    <v-mask @hide="closeAlertBox" :is_show="alertbox.show"></v-mask>
    <v-indicator v-if="is_show_indicator"></v-indicator>
  </div>
</template>
<script>
  import router from '../../router'
  import Alertbox from '../component/AlertBox'
  import Tip from '../component/Tip'
  import Nextstep from '../component/NextStep'
  import Mask from '../component/Mask'
  import bus from '../../bus'
  import config from '../../config';
  import Indicator from '../component/Indicator'
  import cert from '../../cert'
  import tools from '../../tools'

  export default {
    name: 'certinstall',
    data() {
      return {
        title: '安装数字证书',//标题
        nextstep: {
          next: true,
          text: ['下一步', '点击重新安装', '联系客服解决']
        },
        install_step: 1,//数字证书安装步骤，1为正在安装 2为安装成功  3为安装失败
        tel_href: '',//拨打电话链接
        is_show_indicator: false,//是否显示加载图标
        alertbox: {
          content: [],
          show: false,
        },
        is_can_call:true,
      }
    },
    components: {
      'v-tip': Tip,
      'v-nextstep': Nextstep,
      'v-alertbox': Alertbox,
      'v-mask': Mask,
      'v-indicator': Indicator
    },
    methods: {
      closeAlertBox: function () {//关闭弹出框
        this.alertbox.show = false;
      },
      allowNext: function () {//下一步按钮点击事件
        this.is_show_indicator = true;
        callNativeHandler('khh5', {
          "action": "getNetworkType",
          "reqId": "getNetworkType",
          "param": {}
        }, function (data) {
          if (typeof data == 'string') {
            data = JSON.parse(data);
          }
          if (data.param.networkType == '0') {
            self.is_show_indicator = false;
            self.alertbox.content = ['网络连接失败，请重新连接网络'];
            self.alertbox.show = true;
          } else {
            self.is_show_indicator = false;
            tools.stepSync('certinstall');
            var nextlist = JSON.parse(localStorage.getItem('nextList'));
            tools.nextpage = nextlist[nextlist.indexOf('certinstall') + 1];
            tools.afterBackToOpenAccountStep();
          }
        });
      },
      call:function(){
        var self = this;
        this.is_can_call = false;
        setTimeout(function(){
          self.is_can_call = true;
        },1000);
      },
      reinstall: function () {//重新安装证书点击事件
        this.install_step = 1;
        if (cert.processArr[0]) {
          cert.createPkcs10(this, 1);
        } else if (cert.processArr[1]) {
          cert.zhongDengCert(cert.pkcs10, this, 1);
        } else if (cert.processArr[2]) {
          cert.installCert(cert.p7cert, this, 1);
        } else {
          cert.zhongDengCertApplyState(this, 1);
        }
      },
      doNothing: function(){
        return false
      }
    },
    created: function () {
      this.tel_href = 'tel:' + JSON.parse(localStorage.getItem('qsDetail')).qs_hotline;
      bus.$emit('titleName', this.title, 6);
      cert.zhongDengCertApplyState(this, 1);
    }
  }

</script>
<style>
  .certinstall {
    text-align: center;
  }

  .installing {
    width: 100%;
    text-align: center;
    font-size: 0.28rem;
    color: #333;
    margin-top: 0.15rem;
  }

  .cow-icon {
    text-align: center;
    margin-top: 2.5rem;
  }

  .cow-icon img {
    width: 1rem;
    height: 1.6rem;
  }

  .cert-install-status {
    color: #3a83d7;
    font-size: 0.32rem;
  }

  .cert-reinstall,
  .contact-service {
    margin-left: 0.3rem;
  }

  .cert-reinstall {
    position: fixed;
    bottom: 1.4rem;
    background-color: #3a83d7;
    color: #fff;
  }

  .contact-service {
    position: fixed;
    bottom: 0.3rem;
    background-color: #f64d54;
    color: #fff;
  }

  .tel-box {
    position: relative;
  }

  .tel-box a {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
  }

</style>
